def hdl_num(a)
  puts "Combien y a-t-il de handle dans cette array (Hit Enter) ?"
  answer = gets.chomp
 puts "Handles Number : #{a.count}\n\n"
end
def hdl_longest(a)
  puts "Quel est le handle le plus court de cette liste (Hit Enter) ?"
  answer = gets.chomp
a = a.sort_by {|x| x.length}
puts "Le handle le plus court est :  #{a[0]}"
end
def hdl_5_char(a)
  puts "Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)(Hit Enter)"
  answer = gets.chomp
  puts"Handles avec 5 charactères : #{a.count{|x| x.length == 6}}"
end
def hdl_upercase(a)
  puts "Combien commencent par une majuscule (première lettre juste après le @)(Hit Enter) ?"
  answer = gets.chomp
  puts"Handles avec des Majuscules apres le @ : #{a.count {|e| e =~ /^@[A-Z]/}}"
end
def hdl_sort_az(a)
  puts "Trie la liste de handle par ordre alphabétique (Hit Enter)"
  answer = gets.chomp
  puts"Voici les handles classés par ordre alphabétique : "
  sleep (1)
  puts"#{a.sort(&:casecmp)}" 
end
def hdl_sort_length(a)
  puts "Trie la liste de handle (les plus petits en premiers, les plus grands après) (Hit Enter)"
  answer = gets.chomp
  puts "Voici le classment par longueur des Handles en partant de la plus petite : "
  sleep (1)
  puts a.sort_by {|x| x.length}
end
def hdl_find_epenser(a)
  puts "Quelle est la position dans l'array de la personne @epenser ? (Hit Enter)"
  answer = gets.chomp
 puts"La position de @epenser est : #{a.index("@epenser")}"
end
def hdl_characters(a)
  puts "Sors-moi une répartition des handle par taille de ces derniers (Hit Enter)"
  answer = gets.chomp
  i = 0
while i < 17 do
  puts "adresses avec #{i} charactères #{a.count{|x| x.length == i}}"
  i +=1
end
  puts "adresses avec 33 charactères #{a.count{|x| x.length == 33}}"
end
  


def perform(a)
  hdl_num(a)
  hdl_longest(a)
  hdl_5_char(a)
  hdl_upercase(a)
  hdl_sort_az(a)
  hdl_sort_length(a)
  hdl_find_epenser(a)
  hdl_characters(a)
end

perform(a)





